import React, { useContext, useRef, useEffect, useState } from "react";
import ProfileContext from "../context/profile/profileContext";
import { NavLink, Link } from "react-router-dom";
import background from "../../assets/loginbackground.jpeg";
import useSearch from "./useSearch";
import ValidateSearch from "./ValidateSearch";

const SearchForm = () => {
  const profileContext = useContext(ProfileContext);
  const {
    filterProfiles,
    clearFilter,
    filtered,
    loading,
    getAllProfiles,
    AllProfiles,
  } = profileContext;

  const [values, setValue] = useState({
    text: "",
    date: "",
  });

  useEffect(() => {
    if (filtered === null) {
      values.text = "";
      values.date = "";
    }
    getAllProfiles();
  });

  const onChange = (e) => {
    if (values.text !== "" && values.date !== "" ) {
      filterProfiles(e.target.value);
    } else {
      clearFilter();
    }
  };

  //NEW

 
  const handleChange = (e) => {

    const { name, value } = e.target;
    setValue({
      ...values,
      [name]: value,
    });
  };


  //SEARCH FORM
  const mystyle = {
    backgroundImage: `url(${background})`,
    width: "100%",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
  };



  

  // const { Submitform } = GenerationContext;


  const [isSubmitted, setIsSubmitted] = useState(false);
  const Submitforms = () => {
    setIsSubmitted(true);
  }



  const { searchValues, handleChangeSearch, onSubmitSearch, error } = useSearch(
    Submitforms,
    ValidateSearch
  );

  const { name, date } = searchValues;

  return (
    <section className="login" style={mystyle}>
      <div className="login-container">
        <h1>Login</h1>
        <p className="login-subtext"> Sign in to manage your grief page</p>
        <form onSubmit={onSubmitSearch} noValidate>
          <div className="form-control">
            <input
              placeholder="enter name"
              type='text'
              onChange={handleChangeSearch}
              id="name"
              name="name"
              value={name}
            ></input>
            {error.name && (
              <p className="error">{error.name}</p>
            )}
          </div>
          <div className="form-control">
            <input
              onChange={handleChangeSearch}
              type="date"
             
              id="date"
              name="date"
              value={date}
            ></input>
            {error.date && (
              <p className="error">{error.date}</p>
            )}
          </div>
          
          <button type="submit">Fortfahren</button>
        </form>
        <div className="forgot-register">
          <Link className='password-forgot' to="/">DATE</Link>
        </div>
      </div>
    </section>
  );
};

export default SearchForm;
