import { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router";
import AuthContext from "../context/auth/authContext";
import AlertContext from "../context/alert/alertContext";
import ProfileContext from "../context/profile/profileContext";
import ValidateNote from "./ValidateNote";

const useNote = (callback, Validate) => {
  const history = useNavigate();

  const authContext = useContext(AuthContext);
  const alertContext = useContext(AlertContext);
  const profileContext = useContext(ProfileContext);

  const { setAlert } = alertContext;
  const { myerror, clearErrors, isAuthenticated } = authContext;
  const { Messages, addNotes } = profileContext;

  //GENERAL

  const [error, setError] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  //SIGN IN
  const [noteValues, setNoteValues] = useState({
    message: "",
    author: "",
  });

  const handleChangeSign = (e) => {
    const { name, value } = e.target;
    setNoteValues({
      ...noteValues,
      [name]: value,
    });
  };

  const notes = noteValues;

  //   useEffect(() => {
  //     if (isAuthenticated) {
  //      history("/");
  //     }

  //     // eslint-disable-next-line
  //   }, [isAuthenticated, history]);

  //Login

  const onSubmitNote = (e) => {
    setError(ValidateNote(noteValues));

 
      addNotes(notes);
      console.log(notes)


    e.preventDefault();
    setIsSubmitting(true);
  };

  useEffect(() => {
    if (Object.keys(error).length === 0 && isSubmitting) {
      callback();
      // history("/");
    }
    // eslint-disable-next-line
  }, [error]);

  return {
    noteValues,
    onSubmitNote,
    handleChangeSign,
    error,
  };
};

export default useNote;
