import React, { Fragment, useContext, useEffect } from "react";
import mobilelogo from "../../assets/logos/mobilelogo.png";
import desktoplogo from "../../assets/logos/desktoplogo.png";
import { NavLink } from "react-router-dom";
import AuthContext from "../context/auth/authContext";
import ProfileContext from "../context/profile/profileContext";

const Navbar = () => {
  const authContext = useContext(AuthContext);
  const profileContext = useContext(ProfileContext);

  const { getProfiles } = profileContext;

  useEffect(() => {
    getProfiles();

    //eslint-disable-next-line
  }, []);

  const { isAuthenticated, logout, user } = authContext;

  const onLogout = () => {
    logout();
  };

  const authLinks = (
    <div className="authLinks">
      <li className="helloUsername">
        Hello, <span className="username">{user && user.username}</span>{" "}
      </li>
      <li>
        <NavLink className="create-profile-link" to="/upload">
          Create Profile
        </NavLink>
      </li>
      <li>
        <NavLink className="show-profile-link" to="/showcase-all">
          Show Profiles
        </NavLink>
      </li>
      <li>
        <a className='logout-link' onClick={onLogout} href="#!">
          <span className="register">Logout</span>
        </a>
      </li>
    </div>
  );

  const guestLinks = (
    <div className="guestlinks">
      <NavLink className="login" to="/Login">
        Login
      </NavLink>
      <NavLink className="register" to="/Register">
        Register
      </NavLink>
    </div>
  );

  return (
    <nav className="navbar">
      <div className="desktoplogo">
        <img src={desktoplogo} alt="logo"></img>
      </div>
      <div className="mobilelogo">
        <img src={mobilelogo} alt="mobilelogo"></img>
      </div>
      <ul>
        <li>
          <NavLink className="myLink" to="/">
            Home
          </NavLink>
        </li>
        <li>
          <NavLink className="myLink" to="/about">
            About Us
          </NavLink>
        </li>
        <li>
          <NavLink className="myLink" to="/shop">
            Shop
          </NavLink>
        </li>
        <li>
          <NavLink className="myLink" to="/contact">
            Contact Us
          </NavLink>
        </li>
        {/* <li>
          <NavLink className="myLink" to="/showcase">
            Showcase
          </NavLink>
        </li> */}
      </ul>

      {isAuthenticated ? authLinks : guestLinks}

      <div className="hamburger">
        <i className="fas fa-bars"></i>
        <i className="fas fa-times"></i>
      </div>
    </nav>
  );
};

export default Navbar;
