import React, { useContext,useState } from "react";
import background from "../../assets/loginbackground.jpeg";
import useNote from "./useNote";
import ValidateNote from "./ValidateNote";
import generationContext from "../context/generationContext";
import { Link } from "react-router-dom";


const  Notes = () => {
    const [isSubmitted, setIsSubmitted] = useState(false);
  const Submitforms = () => {
    setIsSubmitted(true);
  }



  const { noteValues, handleChangeSign, onSubmitNote, error } = useNote(
    Submitforms,
    ValidateNote
  );

  const { message, author } = noteValues;
    return (
        <div>
            <form onSubmit={onSubmitNote} noValidate>
          <div className="form-control">
            <textarea
              placeholder="Please write a note"
              type="text"
              onChange={handleChangeSign}
              id="message"
              name="message"
              value={message}
            ></textarea>
            {error.message && (
              <p className="error">{error.message}</p>
            )}
          </div>
          <div className="form-control">
            <input
              onChange={handleChangeSign}
              type="text"
              placeholder="Your name"
              id="author"
              name="author"
              value={author}
            ></input>
            
          </div>
          
          <button type="submit">Fortfahren</button>
        </form>
        </div>
    )
}

export default Notes
