// import axios from "axios";
import { useEffect, useState, useContext} from "react";
import { useNavigate } from "react-router";
import AuthContext from "../context/auth/authContext";
import AlertContext from "../context/alert/alertContext";
import ProfileContext from "../context/profile/profileContext";



const useSearch = (callback, ValidateSearch) => {
  const history = useNavigate();

  const authContext = useContext(AuthContext)
  const alertContext = useContext(AlertContext)
  const profileContext = useContext(ProfileContext);
  const {
    filterProfiles,
    clearFilter,
    filtered,
    loading,
    getAllProfiles,
    AllProfiles,
  } = profileContext;


const {setAlert} = alertContext;
  const {register,myerror,clearErrors, isAuthenticated} = authContext

  //GENERAL

  const [error, setError] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  //SIGN UP
  const [searchValues, setValue] = useState({
    name: "",
    date: ""
  });

  //handle change event

  const handleChangeSearch = (e) => {

    const { name, value } = e.target;
    setValue({
      ...searchValues,
      [name]: value,
    });
  };

  //Set to local storage


  //On submit Event

  const users = searchValues;


//   useEffect(() => {

//     if(isAuthenticated){
//      history("/");
//     }
//     if(myerror === 'User already exists') {
//       setAlert(myerror, 'danger')
//       clearErrors()
//     }

//     // eslint-disable-next-line
//   },[myerror, isAuthenticated, history])

//NEW USE EFFECT

useEffect(() => {
    if (filtered === null) {
        searchValues.text = "";
        searchValues.date = "";
    }
    getAllProfiles();
  },[]);


  const onSubmitSearch = (e) => {
    setError(ValidateSearch(searchValues));
    // register(users)
    if (searchValues.text !== "" && searchValues.date !== "" ) {
        filterProfiles(e.target.searchValues);
      } else {
        clearFilter();
      }
    e.preventDefault();
    
    setIsSubmitting(true);
  };

  useEffect(() => {
    if (Object.keys(error).length === 0 && isSubmitting) {
      callback();
      // history("/Login");
    }
    // eslint-disable-next-line
  }, [error]);

  return {
    handleChangeSearch,
    searchValues,
    onSubmitSearch,
    error,
  };
};

export default useSearch;
