import React, { useState } from "react";


const Forgot = () => {


  const [status, setStatus] = useState(false)

  return (
    <div className='container-nav'>
<section className="forgot password" >
      <div className="forgot-container">
        {
          status ? <p>A recovery link has been sent to your email</p> : <p>Please enter your email to reset your password</p>
        }
        
        <form>
          <div className="form-control">
            <input placeholder="please enter your email" type="email"></input>
          </div>
          <button className='recover-button' type="submit">SEND</button>
        </form>
      </div>
    </section>
    </div>
    
  );
};

export default Forgot;
