import React, { useContext, useEffect } from "react";
import { NavLink } from "react-router-dom";
import normalpicture from "../../assets/about2.jpeg";
import ProfileContext from "../context/profile/profileContext";
import Spinner from "../shared/Spinner";

const ShowcaseAll = () => {
  const profileContext = useContext(ProfileContext);
  const { profiles, getProfiles, loading, uploadedFile } = profileContext;

  useEffect(() => {
    getProfiles();
    //eslint-disable-next-line
  }, []);

  console.log(profiles)
  return (
    <section className='allProfiles'>
      {profiles.length !== 0 && !loading ? (
        <div>
          {profiles.map((profile, key) => (
            <div key={profile._id}>
              <div>{profile.firstname}</div>
              <div>{profile.dateOfBirth}</div>
              <div>{new Date(profile.date).toISOString().substring(0, 10)}</div>
              <img alt='' src={profile.imageLink}></img>
            </div>
          ))}
        </div>
      ) : (
        <div>Please create a profile</div>
      )}
    </section>
  );
};

export default ShowcaseAll;
