import React, { Fragment, useState } from "react";
import Message from './Message';
import Progress from './Progress';
import axios from "axios";
import { Link } from "react-router-dom";




function FileUpload() {
  const [file, setFile] = useState("");
  const [filename, setFilename] = useState("Choose File");
  const [uploadedFile, setUploadedFile] = useState({});
  const [message, setMessage] = useState("");
  const [uploadPercentage, setUploadPercentage] = useState(0);

  const onChange = (e) => {
    setFile(e.target.files[0]);
    setFilename(e.target.files[0].name);
    
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post("/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
        onUploadProgress: (progressEvent) => {
          setUploadPercentage(
            parseInt(
              Math.round((progressEvent.loaded * 100) / progressEvent.total)
            )
          );
        },
      });

      // Clear percentage
      setTimeout(() => setUploadPercentage(0), 10000);

      const { fileName, filePath } = res.data;

  

      setUploadedFile({ fileName, filePath });

      setMessage("File Uploaded");
    } catch (err) {
      if (err.response.status === 500) {
        setMessage("There was a problem with the server");
      } else {
        setMessage(err.response.data.msg);
      }
      setUploadPercentage(0);
    }
  };

  return (
    <section className='upload'>

    
    <Fragment>
      {message ? <Message className='messageError' msg={message} /> : null}
      <form onSubmit={onSubmit} >
        <div >
          <input
            type='file'
            id='customFile'
            onChange={onChange}
            className='fileInput'

          />
    
        </div>
        <input
          type='submit'
          value='Upload'
          className='upload-button'
         
        />
      </form>
      {uploadedFile ? (
        <div className='uploadedFile-container' >
          <div className='picture-and-name-container' >
            
            <img style={{ width: '300px', height:'300px'}} src={  uploadedFile.filePath} alt='' />
         
            <h3 >{uploadedFile.fileName}</h3>
           
          </div>
         
          <Link className='create-profile-link' to='/showcase'>Create Profile</Link>
       
        </div>
        
      ) : null}
    </Fragment>
    </section>
  );
}

export default FileUpload;
