import React, { useContext } from "react";
import { NavLink } from "react-router-dom";
import AuthContext from "../context/auth/authContext";
import ProfileContext from "../context/profile/profileContext";
import profilephoto from '../../assets/about1.jpeg'

const UserProfile = () => {
  const authContext = useContext(AuthContext);
  const profileContext = useContext(ProfileContext);

  const {
    profiles,
    getProfiles,
    loading,
    deleteProfile,
    clearCurrent,
    updateProfile,
    setCurrent,
  } = profileContext;
  const { user } = authContext;

  console.log(user);
  return (
    <section className="userprofile">
      <div className="userprofileUpper"></div>
      <div className='profilephoto'>
          <img src={profilephoto} alt='profilephoto'  ></img>
      </div>
      <div className='username'> 
        <p>{user.username}</p>
      </div>

      <div>
        <div>
          <NavLink to="/showcase-all">Show Profiles</NavLink>
        </div>
        <div>
          <NavLink className="create-profile-link" to="/upload">
            ADD OBITUARY
          </NavLink>
        </div>

        <p>Delete My Account</p>
      </div>
    </section>
  );
};

export default UserProfile;
